import react, { Fragment, useEffect, useState } from 'react';

import axios from 'axios'

const Profile = () => {
    const [user ,setUser] = useState([])

  useEffect(() =>{
    async function getUserData(){
        try{
          const user = await axios.get('http://localhost:8000/company/showUser')
          console.log(user.data)
          setUser(user.data);
        }catch(err){
          console.log(err)
        }
    }
    getUserData();
  },[])
  return (
    <Fragment>
    <h1>Your Experience</h1>
        <div className='container-fulid mt-12'>
            <div className='row text-center'> { 
                user.map((user) => {
                    return (
                        <>
                            <div className='row my-2'>
                                        {/*for image only*/}
                                        <div class="card">
                                            <img class="card-img-top" src={'/uploads/'+ user.profileImage} alt="Card image cap" />
                                                <div class="card-body">
                                                    <h5 class="card-title">{user.email}</h5>
                                                    <p class="card-text"><small class="text-muted">{user.name}</small></p>
                                            
                                                    <button className='btn btn-primary'
                                                    type="button">Book Now</button>
                                                </div>
                                        </div>
                            </div> 
                        </>
                      ) 
                })  
            }   
            </div>
        </div>       
</Fragment>     
  )
 }

export default Profile
