import react, { Fragment, useEffect, useState } from 'react';
import { Navigate,useNavigate} from 'react-router-dom';

import axios from 'axios'

const Profile = () => {
  const navigate = useNavigate();
    const [user ,setUser] = useState([])

    const handleDelete = async (id) => {
        alert(id);
        const data = await axios.post(`http://localhost:8000/company/deleteUser/${id}`)
        alert(data)
    }
    const navigateToUpdate = (id) => {
      navigate(`/update/${id}`,{state:user._id})
    };
  
  return (
    <Fragment>
    <h1>Your Experience</h1>
      <div className="container">
        <div className="row">
                <div class="col-sm-6 mt-5">
                <h2 class="alert alert-danger text-center">List of User</h2>
                
                  <table class="table table-success table-striped">
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Phone</th>
                        <th>Email</th>
                        <th>Update</th>
                        <th>Delete</th>
                        <th>Photo</th>
                    </tr>
            { 
                user.map((user) => {
                    return (
                        <>  
                        <tr>
                        <td>{user._id}</td>
                        <td>{user.name}</td>
                        <td>{user.phone}</td>
                        <td>{user.email}</td>
                        <td><button className='btn btn-primary' type="button"
                        onClick={ function handleClick()  {
                          navigate(`/update/${user._id}`,
                          {state:
                              {id:1,name:curElem.show.name,score:curElem.score,summary:curElem.show.summary}})}}
                              >Update</button></td>
                        <td><button className='btn btn-danger' type="button" onClick={ 
                          () => handleDelete(user._id)
                        }>                 
                        Delete</button></td>
                        <td><img class="card-img-top" src={'/uploads/'+ user.profileImage}/></td>
                      </tr>
                        </>
                      ) 
                })  
           
            }  
            </table>
            </div>
        </div>    
        </div> 
</Fragment>     
  )
 }

export default Profile
