import{ useEffect, useState} from 'react'
import axios from 'axios'

const Profile = () => {
    const [user ,setUser] = useState([])

  useEffect(() =>{
    async function getUserData(){
        try{
          const user = await axios.get('http://localhost:8000/company/showUser')
          console.log(user.data)
          setUser(user.data);
        }catch(err){
          console.log(err)
        }
    }
    getUserData();
  },[])
  return (
    <>
