import react, { Fragment, useEffect, useState } from 'react';

import axios from 'axios'

const Profile = () => {
    const [user ,setUser] = useState([])

  useEffect(() =>{
    async function getUserData(){
        try{
          const user = await axios.get('http://localhost:8000/company/showUser')
          console.log(user.data)
          setUser(await user);
        }catch(err){
          console.log(err)
        }
    }
    getUserData();
  },[])
  user.map((user) => {
  return (
    <Fragment>
    <h1>Your Experience</h1>
    <div class="container">
        <div class="row">
           
            <div class="col-sm-6 mt-5">
                <h2 class="alert alert-danger text-center">List Student</h2>
                <table class="table table-success table-striped">
                    <tr>
                        <th>Name</th>
                        <th>Phone</th>
                        <th>Email</th>
                        <th>Photo</th>
                    </tr>
                    <tr>
                        <td>{user.name}</td>
                        <td>{user.phone}</td>
                        <td>{user.Email}</td>
                        <td></td>
                        <td>
                            <form action="/student/edit/<%= item._id %>" method="get" class="d-inline">
                                <button type="submit" class="btn btn-warning">
                                    <i class="fas fa-pen-square text-info"></i>
                                </button>
                              </form>
                            
                              <form action="/student/delete/<%= item._id %>" method="post" class="d-inline">
                                <button type="submit" class="btn btn-warning">
                                  <i class="fas fa-trash text-danger"></i>
                                </button>
                              </form>
                            
                        </td>
                    </tr>
                  </table>
            </div>
        </div>
    </div>
</Fragment>     
  )
})
 }


export default Profile
