    const [user ,setUser] = useState([])

  useEffect(() =>{
    async function getUserData(){
        try{
          const user = await axios.get('http://localhost:8000/company/showUser')
          console.log(user.data)
          setUser(user.data);
        }catch(err){
          console.log(err)
        }
    }
    getUserData();
  },[])
  return (
    <>
     <div>Home</div>
     <h1>User Data</h1>
