                        show.map((curElem) => {
                            return (
                                <>
                                    <div className='row my-5'>
                                                {/*for image only*/}
                                                <div class="card">
                                                    <img class="card-img-top" src={'/uploads/'+user.profileImage} alt="Card image cap" />
                                                </div>
                                    </div> 
                                </>
                                    ) 
                        })  
                    }   
                    </div>
                </div>       
          </>
        )
      })
     }
    </>
  )
}

export default Profile
