import react, { Fragment, useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';

import axios from 'axios'

const Profile = () => {
    const [user ,setUser] = useState([])

    const handleDelete = (id) => {
        alert(id)
    }

  useEffect(() =>{
    async function getUserData(){
        try{
          const user = await axios.get('http://localhost:8000/company/showUser')
          console.log(user.data)
          setUser(user.data);
        }catch(err){
          console.log(err)
        }
    }
    getUserData();
  },[])
  return (
    <Fragment>
    <h1>Your Experience</h1>
      <div className="container">
        <div className="row">
                <div class="col-sm-6 mt-5">
                <h2 class="alert alert-danger text-center">List of User</h2>
                
                  <table class="table table-success table-striped">
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Phone</th>
                        <th>Email</th>
                        <th>Update</th>
                        <th>Delete</th>
                        <th>Photo</th>
                    </tr>
            { 
                user.map((user) => {
                    return (
                        <>  
                        <tr>
                        <td>{user._id}</td>
                        <td>{user.name}</td>
                        <td>{user.phone}</td>
                        <td>{user.email}</td>
                        <td className='d-flex justify-content-between'><button className='btn btn-primary' type="button" >Update</button>
                        </td>
                        <td></td>
                        <td><img class="card-img-top" src={'/uploads/'+ user.profileImage}/></td>
                      </tr>
                        </>
                      ) 
                })  
           
            }  
            </table>
            </div>
        </div>    
        </div> 
</Fragment>     
  )
 }

export default Profile
