import React, { Component, useState } from 'react';
import axios from 'axios';
const url='http://localhost:8000/company/createUser';


const Contact = () => {

    const [Employee,setEmployee] = useState([])
    const [data ,setData] = useState({
        name:"",
        phone:"",
        email:"",
        password:""
    })
    const Eventchange = (event) =>{
        const {name,value} = event.target;

        setData((preval) => {
            return {
                ...preval,
                [name]:value,
            }
        });
    }
    const  formSubmit= async (e)=>{
        e.preventDefault();
        const Employee = await axios.post(url,{
            name : data.name , 
            phone : data.phone,
            email : data.email,
           password : data.password,
           profileImage: data.profileImage

        })  
        console.log(Employee.data);
        setEmployee(Employee.data)
        
    }
    return(
        <div>
            <div className="my-5">
                <h1 className='text-center'>SignUp for sharing Travel Experience With Us</h1>
            </div>
            <div className='container contact_div'>
                <div className='row'>
                    <div className='col-md-6 col-10 mx-auto'>
                        <form onSubmit={formSubmit}>
                                <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">Name</label>
                                <input type="text" 
                                 class="form-control" 
                                 id="exampleFormControlInput1"
                                 name="name"
                                 value={data.name}
                                 onChange={Eventchange}
                                 placeholder="Enter Your Name"
                                 required 
                                 />
                                </div>
                               
                                <div class="mb-3">
                                <label for="exampleFormControlInput1"   class="form-label">Phone</label>
                                <input type="text"
                                 class="form-control" 
                                 id="exampleFormControlInput1"
                                 name="phone"
                                 value={data.phone}
                                 onChange={Eventchange}
                                 placeholder="Mobile Number"
                                 required  pattern="[7-9]{1}[0-9]{9}" />
                                </div>

                                <div class="mb-3">
                                <label for="exampleFormControlInput1"   class="form-label">Profile Picture</label>
                                <input type="file"
                                 class="form-control" 
                                 id="exampleFormControlInput1"
                                 name="profileImage"
                                 value={data.profileImage}
                                 onChange={Eventchange}
                                 required  />
                                </div>

                                <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">Email address</label>
                                <input type="email"
                                 class="form-control" 
                                 id="exampleFormControlInput1"
                                 name="email"
                                 value={data.email}
                                 onChange={Eventchange}
                                 placeholder="Enter Your Email address" required
                                 pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$" />
                                </div>

                                <div class="mb-3">
                                <label for="exampleFormControlInput1" class="form-label">Password</label>
                                <input type="password"
                                 class="form-control" 
                                 id="exampleFormControlInput1"
                                 name="password"
                                 value={data.password}
                                 onChange={Eventchange}
                                 placeholder="Enter Password" required
                                 />
                                </div>
                               

                                <div class="col-12">
                                    <button class="btn btn-outline-primary" type="submit">
                                        Submit form
                                    </button>
                                </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>  
    );

}
export default Contact;
